"""main game loop"""from bearlibterminal import terminalimport variables as varimport obj_def as odfrom handle_keys import handle_keysfrom render_map import render_mapfrom GameObject import GameObjectterminal.open()a_tree = GameObject(od.tree_01['name'], od.tree_01['layers'], 16, 15)a_tree2 = GameObject(od.tree_02['name'], od.tree_02['layers'], 18, 15)while var.player_action != 'exit':    # get the action of the player, if any    var.player_action = handle_keys()    # print the map    render_map()    # print the player    terminal.color(terminal.color_from_name('orange'))    terminal.printf(20, 10, 'Hello World')    terminal.printf(20, 11, '[font=typed]Hello World')    terminal.put_ext((var.player_x - var.map_offset_x)*2, var.player_y - var.map_offset_y, 0, 0, var.player_char)    a_tree.draw()    a_tree2.draw()    # refresh it to the screen    terminal.refresh()    # erase the player    terminal.printf(var.player_x - var.map_offset_y, var.player_y - var.map_offset_y, ' ')    if var.player_action == 'exit':        terminal.close()