"""main game loop"""from bearlibterminal import terminalimport variables as varimport obj_def as odfrom handle_keys import handle_keysfrom render_map import render_mapfrom Entity import Entityterminal.open()player = Entity(od.player['name'], od.player['layers'], var.player_x, var.player_y, od.player['blocks'])var.player = playera_tree = Entity(od.tree_01['name'], od.tree_01['layers'], 16, 15, od.tree_01['blocks'])a_tree2 = Entity(od.tree_02['name'], od.tree_02['layers'], 18, 15, od.tree_02['blocks'])var.entities.append(a_tree)var.entities.append(a_tree2)var.entities.append(var.player)while var.player_action != 'exit':    # get the action of the player, if any    var.player_action = handle_keys()    # print the map    render_map()    # print entities    for entity in var.entities:        entity.draw()    # refresh it to the screen    terminal.refresh()    # erase the entities    for entity in var.entities:        entity.clear()    if var.player_action == 'exit':        terminal.close()